import classNames from 'classnames/bind';
import styles from './item-list.module.scss';
import { useState } from 'react';
const cx = classNames.bind(styles);
function ItemList({ index, MSSV, name, faculty, ondelete, onchange }) {
    const [show, setshow] = useState(false);
    return (
        <>
            <tr>
                <td>{index}</td>
                <td>{MSSV}</td>
                <td>{name}</td>
                <td>{faculty}</td>
                <td>
                    <button onClick={ondelete}>
                        <i className={cx('fas fa-trash')}></i>
                    </button>
                </td>
                <td>
                    <button
                        className={cx('sua')}
                        onClick={() => {
                            setshow(!show);
                        }}
                    >
                        Sửa
                    </button>
                </td>
            </tr>
            {show && (
                <tr>
                    <td>{index}</td>
                    <td>
                        <input type="text" className={cx('input-add-list')} placeholder="Nhập mã mới" id="mssv" />
                    </td>
                    <td>
                        <input type="text" className={cx('input-add-list')} placeholder="Nhập tên mới" id="name" />
                    </td>
                    <td>
                        <input type="text" className={cx('input-add-list')} placeholder="Nhập khoa mới" id="khoa" />
                    </td>
                    <td>
                        <button
                            className={cx('sua')}
                            onClick={() => {
                                setshow(!show);
                            }}
                        >
                            Hủy
                        </button>
                    </td>
                    <td>
                        <button
                            className={cx('sua')}
                            onClick={() => {
                                if (
                                    document.getElementById('mssv').value.length > 0 ||
                                    document.getElementById('name').value.length > 0 ||
                                    document.getElementById('khoa').value.length > 0
                                ) {
                                    let id = MSSV;
                                    if (document.getElementById('mssv').value.length > 0) {
                                        id = document.getElementById('mssv').value;
                                    }
                                    let ten = name;
                                    if (document.getElementById('name').value.length > 0) {
                                        ten = document.getElementById('name').value;
                                    }
                                    let khoa = faculty;
                                    if (document.getElementById('khoa').value.length > 0) {
                                        khoa = document.getElementById('khoa').value;
                                    }
                                    onchange(`${id}`, `${ten}`, `${khoa}`);
                                    setshow(!show);
                                } else {
                                    alert('Vui lòng điền ít nhất 1 thông tin');
                                }
                            }}
                        >
                            Lưu
                        </button>
                    </td>
                </tr>
            )}
        </>
    );
}

export default ItemList;
